import './App.css';
import React, { useState } from 'react';
// import pic from './assets/pic.jpg';
import Card from './Card';
import './Card.css';
import { heads2024 } from './Data/heads2024';
import {heads2023} from './Data/heads2023.js';
import {heads2022} from './Data/heads2022.js';
import {sms2024} from './Data/sms2024.js';
import {sms2023} from './Data/sms2023.js';
import {sms2022} from './Data/sms2022.js';



const dataMap = {
    heads: {
        2024: heads2024,
        2023: heads2023,
        2022: heads2022,
    },

    sms: {
        2024: sms2024,
        2023: sms2023,
        2022: sms2022,
    },
};



const AboutUs = () => {


  const [expandYear, setExpandedYear] = useState('2024');
  const [showHeads, setShowHeads] = useState(true);



  const toggleYear = (year) => {
    if(expandYear === year){
      setExpandedYear(null);
    } else {
      setExpandedYear(year);
    }
  };


 const toggleShowHeads =(show) => {
  setShowHeads(show);
 };


 const renderContent =(year) => {

  const selectedData = showHeads? dataMap.heads[year]: dataMap.sms[year];


return (
<>

  <div className="tab">
        
  <button className={`buttonCategory ${showHeads ? 'active' : ''}`} onClick={() => toggleShowHeads(true)}>Heads</button>
  <button className={`buttonCategory ${!showHeads ? 'active' : ''}`} onClick={() => toggleShowHeads(false)}>SMs</button>


</div>





<div className="card-container">


  {selectedData.map((member, index) => (
       <Card
       key={index}
       name= {member.name}
       designation={member.designation}
       photo={member.photo}
       facebook={member.facebook}
       linkedin={member.linkedin}
       />
  )
  
  )}

  </div>      

 </>
);

 };




// const handleYear2024 = () => {
//   setYear('2024');
// }

// const handleYear2023 = () => {
//   setYear('2023');
// }

// const handleYear2022 = () => {
//   setYear('2022');
// }




  return (
    <div className="App">

      <div className="topArea">


        <div className="title"><h1>Student Welfare Group</h1></div>
        <p>Student Welfare Group helps students in improving their skills, attitude and resources necessary to both succeed in the college environment as well as to pursue productive and satisfying lives by organizing different activities on the campus. Our Group strives to contribute to the overall educational mission of the Institute by facilitating the academic, emotional, social and vocational development of the entire campus community.</p>


      </div>


<div className="heading"><h1>Our Team</h1></div>


      <div className="yearSection">
  
      {  ['2024', '2023', '2022'].map((year) => (
          <div key={year} className="yearCollapsible">

            <button type="button" className={`collapsible ${expandYear === year ? 'active' : ''}`} onClick={() => toggleYear(year)}>
              {year}
            </button>

            {expandYear === year && ( 
              <div className={`content ${expandYear === year ? 'show' : ''}`}>{renderContent(year)}</div>
             )}
          </div>
        ))

      }

      </div>


      </div>

    
  );
};


export default AboutUs;
