import React from 'react';
import { FaFacebook } from 'react-icons/fa';
import './Card.css';

const Card = ({ facebook }) => (
  <div className="card">
    <div className="socialMedia">
      {facebook && (
        <a href={facebook} target="_blank" rel="noopener noreferrer">
          <FaFacebook className="social-icon facebook" style={{ color: 'blue', fontSize: '24px' }} />
        </a>
      )}
    </div>
  </div>
);

export default Card;
