import './App.css';
import React, { useState } from 'react';
import pic from './assets/pic.jpg';


function App() {

  const [year, setYear] = useState('');

  const handleYear2024 = () => {
    setYear('2024');
  }

  const handleYear2023 = () => {
    setYear('2023');
  }

  const handleYear2022 = () => {
    setYear('2022');
  }





  return (
    <div className="App">

<div className="heading"><h1>Our Team</h1></div>

      <div className="yearButtons">

        <button className={`year ${year==='2024' ? 'active' : ''}`} onClick={handleYear2024}>2024</button>
        <button className={`year ${year==='2023' ? 'active' : ''}`} onClick={handleYear2023}>2023</button>
        <button className={`year ${year==='2022' ? 'active' : ''}`} onClick={handleYear2022}>2022</button>


      </div>


      <div className="tab">

        <button className='buttonCategory'>Heads</button>
        <button className='buttonCategory'>SMs</button>


      </div>




      <div className="info">


        <div className="card">


        <div className="image"><img src={pic} alt="" /></div>
        <p className="name">ABC</p>
        <p className="portfolio">Head</p>
        <div className="socialMedia">

        <a href="https://www.facebook.com/akshat.kapoor.9461?mibextid=ZbWKwL" class="facebook">
            <i class="fa fa-facebook"></i>
        </a>
        <a href="https://www.linkedin.com/in/akshat-kapoor-7955b6259" class="linkedin">
            <i class="fa fa-linkedin"></i>
        </a>

        </div>

      </div>




      </div>









    _</div>
  );
}

export default App;
